import time
import pyperclip
import sys
def clipboard():
    last_clipboard = None
    while True:
        current_clipboard = pyperclip.paste()
        if current_clipboard != last_clipboard:
            print(current_clipboard)
            sys.stdout.flush()
            last_clipboard = current_clipboard
        time.sleep(0.5)  
if __name__ == "__main__":
    clipboard()

